import React, { Component } from 'react';
import SplitPane from 'react-split-pane';
import { Button, FormGroup, FormControl, FormLabel } from 'react-bootstrap';
import axios from 'axios';

class Home extends Component {
	constructor(props) {
	    super(props);
	    this.state = {
	      item_name: '',
	      calorie_count: '',
	      quantity: ''
	    };
  	}

	validate() {
		return this.state.item_name.length > 0 && this.state.calories.length > 0;
	} 

	handleSubmit = (event) => {
	    event.preventDefault();
	    axios.post('http://localhost:4000/add', {
	      item_name: this.state.item_name,
	      password: this.state.password
	    })
	    .then(res => console.log(res.data));

	    this.setState({
	        item_name: '',
	      	calories: '',
	      	quantity: ''
	    });
	}

	handleChange = (event) => {
	    this.setState({
	      [event.target.id]: event.target.value
	    });
	}


  	render() {
    	return (
	    	<div>
	        	<SplitPane split="vertical" minSize={50} defaultSize={600}>
	        	    <div>
	        	    	<h4 style={h4Style}>Items</h4>
	        	    </div>
     				<div>
     					<h4 style={h4Style}>Add Item</h4>
     					<form onSubmit={this.handleSubmit}>
				          <FormGroup controlId="item_name" bssize="large">
				            <FormLabel>Item</FormLabel>
				            <FormControl
				              autoFocus
				              type="string"
				              placeholder="required"
				              onChange={this.handleChange}
				              value={this.state.item_name}
				            />
				          </FormGroup>
				          <FormGroup controlId="quantity" bssize="large">
				            <FormLabel>Quantity</FormLabel>
				            <FormControl
				              type="number"
				              placeholder="0"
				              onChange={this.handleChange}
				              value={this.state.quantity}
				            />
				          </FormGroup>
				          <FormGroup controlId="calories" bssize="large">
				            <FormLabel>Calories</FormLabel>
				            <FormControl
				            	type="number"
				          		placeholder="required"
				            	onChange={this.handleChange}
				            	value={this.state.calories}
				            />
				          </FormGroup>
				          <FormGroup controlId="expiry_date" bssize="large">
				            <FormLabel>Expiry Date</FormLabel>
				            <FormControl
				            	type="date"
				          		placeholder="required"
				            	onChange={this.handleChange}
				            	value={this.state.calories}
				            />
				          </FormGroup>
				          <Button block type="submit" bssize="large" disabled={!this.validate()}>
				            Add Item
				          </Button>
				        </form>
     				</div>
 				</SplitPane>
	    	</div>
    	);
    }
}

const h4Style = {
  background: '#ccccff',
  color: '#ffffff',
  textAlign: 'center',
  padding: '0px',
  bottomMargin: '5px'
}

const formStyle = {
	textAlign: 'center',
	padding: '05px',
  	bottomMargin: '5px'
}

export default Home;
