import React, { Component } from 'react';
import { Button, FormGroup, FormControl, FormLabel } from 'react-bootstrap';
import {withRouter} from 'react-router-dom';
import axios from 'axios';

class Login extends Component {
  constructor(props) {
    super(props);
    this.state = {
      email: "",
      password: ""
    };
  }

  validate() {
    return this.state.password.length > 0 && this.state.email.length > 0;
  }

  handleSubmit = (e) => {
    e.preventDefault();
    const credentials = {
            name: this.state.email,
            port: this.state.password
    }
    axios.post('http://localhost:4000/register', credentials)
    .then(res => console.log(res.data));

    this.setState({
        email: '',
        password: ''
    });
  }

  handleChange = (e) => {
    this.setState({
      [e.target.id]: e.target.value
    });
  }

  render() {
    return (
      <div>
        <form onSubmit={this.handleSubmit}>
          <FormGroup>
            <FormLabel>Email</FormLabel>
            <FormControl autoFocus type="email" onChange={this.handleChange} value={this.state.email} bssize="large"/>
          </FormGroup>
          <FormGroup>
            <FormLabel>Password</FormLabel>
            <FormControl type="password" onChange={this.handleChange} value={this.state.password}/>
          </FormGroup>
          <Button block disabled={!this.validate()} type="submit">
            Log In
          </Button>
        </form>
      </div>
    );
  }
}

export default Login
