import React, { Component } from 'react';
import SplitPane from 'react-split-pane';
import { Button, FormGroup, FormControl, FormLabel } from 'react-bootstrap';

class Home extends Component {
	constructor(props) {
	    super(props);
	    this.state = {
	      item_name: "",
	      calories: "",
	      quantity: ""
	    };
  	}

  	render() {
    	return (
	    	<div>
	        	<SplitPane split="vertical" minSize={50} defaultSize={600}>
	        	    <div>
	        	    	<h4 style={h4Style}>Items</h4>
	        	    </div>
     				<div>
     					<h4 style={h4Style}>Add Item</h4>
     					<form onSubmit={this.handleSubmit}>
				          <FormGroup controlId="email" bssize="large">
				            <FormLabel>Email address</FormLabel>
				            <FormControl
				              autoFocus
				              type="email"
				              placeholder="Enter email"
				              onChange={this.handleChange}
				              value={this.state.email}
				            />
				          </FormGroup>
				          <FormGroup controlId="password" bssize="large">
				            <FormLabel>Create Password</FormLabel>
				            <FormControl
				              type="password"
				              placeholder="Password"
				              onChange={this.handleChange}
				              value={this.state.password}
				            />
				          </FormGroup>
				          <FormGroup controlId="confirm" bssize="large">
				            <FormLabel>Confirm Password</FormLabel>
				            <FormControl
				              type="password"
				              onChange={this.handleChange}
				              value={this.state.confirm}
				            />
				          </FormGroup>
				          <Button block type="submit" bssize="large" disabled={!this.validate()}>
				            Register
				          </Button>
				        </form>
     				</div>
 				</SplitPane>
	    	</div>
    	);
    }
}

const h4Style = {
  background: '#ccccff',
  color: '#ffffff',
  textAlign: 'center',
  padding: '0px',
  bottomMargin: '5px'
}

export default Home;
